import React from "react";

const ClassCard = (props) => {
  return (
    <div class="ui cards">
      <div
        class="ui card"
        style={{
          backgroundColor: "#37236A",
          height: "30vh",
          minHeight: "200px",
          width: "20vh",
          minWidth: "50px",
        }}
      >
        <div class="content">
          <div class="header" style={{ color: "white", textAlign: "center" }}>
            {props.classCode}
          </div>
          <hr style={{ height: "2px", backgroundColor: "white" }} />
          <div
            class="description"
            style={{ color: "white", textAlign: "center" }}
          >
            {props.classDesc}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClassCard;
